//ng-table (ng-table.com) controller
MainCtrl.$inject = ['$scope', '$filter', '$http', 'NgTableParams'];

function MainCtrl($scope, $filter, $http, NgTableParams) {
  var self = this;

  self.tableParams = new NgTableParams({
  }, {
    filterDelay: 0,

    getData: function (params) {
      var url = '/api/now/table/incident';
      var queryOptions = {
        sysparm_display_value: 'true',
        sysparm_exclude_reference_link: 'true',
        sysparm_fields: 'number,urgency,caller_id,opened_at,state',
        sysparm_query: '',
		sysparm_limit: params.count(),
		sysparm_offset: (params.page()-1) * params.count()
      };
      if (params.hasFilter()){
		for (var field in params.filter()){
			queryOptions.sysparm_query = field + 'LIKE' + params.filter()[field] + '^';	
		}
      }
      if (Object.keys(params.sorting()).length != 0){
		  for (var field in params.sorting()){
			  var direction = (params.sorting()[field] == 'desc') ? 'ORDERBYDESC' : 'ORDERBY';
			  queryOptions.sysparm_query += direction + field;
			  /*queryOptions.sysparm_order = key;
			  queryOptions.sysparm_order_direction = params.sorting()[key];*/
		  }
      }
      return $http.get(url, {
        params: queryOptions,
        headers: global_headers
      })
        .then(function (res) {
            var vals = [];
            var resArray = res.data.result;
            for (var i = 0; i < resArray.length; i++) {
              vals.push({
                "number": resArray[i].number,
                "urgency": resArray[i].urgency,
                "caller_id": resArray[i].caller_id,
                "opened_at": new Date(resArray[i].opened_at),
                "state": resArray[i].state
              });
            }
			params.total(res.headers('X-Total-Count'));
            return vals;
          }, function (reason) {
            //$defer.reject();
          }
        );
    }
  });
}

//ui.bootstrap controller
appController.$inject = ['$scope','$uibModal'];
function appController($scope, $uibModal) {

      $scope.result = null;

      $scope.openDialog = function () {
         var modalInstance = $uibModal.open({
			templateUrl: "/x_20458_mobi_chord_modalDialog.do?sysparm_direct=true",
            controller: "dialogController",
            //size: "sm",
            resolve: {
               params: function () {
                  return {
                     name: "John",
                     age: 32
                  };
               }
            }
         });

         modalInstance.result.then(function (result) {
               $scope.result = result;
            }, function () {
               console.log("Dialog dismissed");
            });
      };
}

dialogController.$inject = ['$scope'];
function dialogController ($scope) {
	var params = $scope.$resolve.params;

	$scope.name = params.name;
	$scope.age = params.age;

	$scope.cancel = function () {
		$scope.$dismiss();
	};

	$scope.ok = function () {
		var retObj = {
			name: $scope.name,
			age: $scope.age,
			profession: "Car Mechanic",
			yearsOfExp: 3
		};
		$scope.$close(retObj);
	};
}
